<?php
namespace frontend\components;
use yii\base\Component;
use Yii;

class MetaComponent extends Component{

    public $keywords = 'keyword ที่ต้องการค้นหาเช้น , รับเขียนโปรแกรม , รับทำเว็บไซต์';
    public $description = 'ใส่รายละเอียดเว็บไซต์ของคุณ';

    public $image = 'data:image/jpeg;base64,';

    public function displaySeo(){

        Yii::$app->view->registerMetaTag([
            'name' => 'description',
            'content' => $this->description,
        ]);
        Yii::$app->view->registerMetaTag([
            'name' => 'keywords',
            'content' => $this->keywords,
        ]);
        Yii::$app->view->registerMetaTag([
            'name' => 'og:description',
            'content' => $this->description,
        ]);
        Yii::$app->view->registerMetaTag([
            'name' => 'og:image',
            'content' => $this->image,
        ]);
        Yii::$app->view->registerMetaTag([
            'name' => 'twitter:description',
            'content' => $this->description,
        ]);

        Yii::$app->view->registerMetaTag([
            'name' => 'twitter:image',
            'content' => $this->image,
        ]);

    }
}